            res := TWOPI - res;
        end if;
    end if;
    
    return res;
    
    end atan2_lut;
begin

-- Continous assignements
process(clk_CI)
begin
   if rising_edge(clk_CI) then
     if (en_DI = '0') then
       udiff1 <= (others => '0');
       udiff2 <= (others => '0');
       diff1_sign <= '0';
       diff2_sign <= '0';
       sat_tmp <= '1';
       interf_flag_tmp <= '0';
       is_zero <= '0';
       dcssat_tmp <= '0';
       alu_div_top_DO <= (others => '0');
       alu_div_bot_DO <= (others => '0');
       alu_delayed_DO <= (others => '0');
     else
		if pulse_DI='1' then
			pulse_div_DO <= '1';
			diff1_sign <= sign0_DI;
			diff2_sign <= sign1_DI;
			-- check for swap
			if udiff0_DI < udiff1_DI then
				udiff1 <= unsigned(udiff1_DI);
				udiff2 <= unsigned(udiff0_DI);
				diff_swap <= '1';
				if unsigned(udiff1_DI) = 0 then
					is_zero <= '1';
				else
					is_zero <= '0';
				end if;
			else
				udiff1 <= unsigned(udiff0_DI);
				udiff2 <= unsigned(udiff1_DI);
				diff_swap <= '0';
				if unsigned(udiff0_DI) = 0 then
					is_zero <= '1';
				else
					is_zero <= '0';
				end if;
			end if;                        
			
			-- propagate the sign to the second pipeline stage
			alu_delayed_DO(35) <= diff1_sign;
			alu_delayed_DO(34) <= diff2_sign;
			alu_delayed_DO(32) <= is_zero;
			alu_delayed_DO(33) <= diff_swap;
			
			alu_delayed_DO(31 downto 0) <= std_logic_vector((31 downto 24 => '0') & udiff1*udiff1 + udiff2*udiff2);
			alu_div_top_DO <= std_logic_vector(udiff2 & (11 downto 0 => '0'));
			alu_div_bot_DO <= std_logic_vector(udiff1);
	
			-- saturation flag
			sat_tmp <= sat0_DI or sat1_DI;
			alu_delayed_DO(36) <= sat_tmp;
			
			-- interf flag
			interf_flag_tmp <= interf_flag_DI;
			alu_delayed_DO(38) <= interf_flag_tmp;
			
			-- dcs saturation
			if dcs_sat_DI = '1' then
				dcssat_tmp <= '1';
			else
				dcssat_tmp <= '0';
			end if;
			alu_delayed_DO(37) <= dcssat_tmp;
		else -- clk, but no pulse
			pulse_div_DO <= '0';
		end if;
		if pulse_div_DI='1' then
			-- Assign function inputs
			sqroot_in <= unsigned(alu_delayed_DI(31 downto 0));
			index <= unsigned(alu_div_res_DI);
			diff1_sign_d <= alu_delayed_DI(35);
			diff2_sign_d <= alu_delayed_DI(34);
			diff_swap_d <= alu_delayed_DI(33);
			is_zero_d <= alu_delayed_DI(32);
			sat_tmp_d <= alu_delayed_DI(36);
			dcssat_tmp_d <= alu_delayed_DI(37);
			interf_flag_tmp_d <= alu_delayed_DI(38);
		end if;
      end if;
    end if;
end process;

-- Amplitude calculation
sqroot_out <= sqrt(sqroot_in);
atan2_out <= atan2_lut(index, diff2_sign_d, diff1_sign_d, diff_swap_d, is_zero_d);

-- Output Registers: Amplitude, Distance, Saturation
process (clk_CI)
begin
	if rising_edge(clk_CI) then
	  if (en_DI = '0') then
        outamp <= (others => '0');
        outdist <= (others => '0');
        outsat <= '1';
        dcssat <= '1';
        minIndex_DO <= '0';
        interf_flag_DO <= '0';
      elsif pulse_out_DI='1' then
		outamp <= shift_right(sqroot_out*corr_a_DI,12)(12 downto 0); -- Multiply by 0....2 // divide sqrt by 2
        outdist <= shift_right(atan2_out*corr_d_DI,11)(16 downto 0); -- Multiply by 0....2
		outsat <= sat_tmp_d;
		dcssat <= dcssat_tmp_d;
		interf_flag_DO <= interf_flag_tmp_d;
		if (index < unsigned(minIndex_DI)) then 
		  minIndex_DO <= '1';
		else
		  minIndex_DO <= '0';
		end if; 
	 end if;
  end if;
end process;

dist_DO <= std_logic_vector(outdist(15 downto 0));
alu_sat_DO <= outdist(16) or outamp(12);
amp_DO  <= std_logic_vector(outamp(11 downto 0)); 
dcssat_DO <= dcssat;
chipsat_DO <= outsat;

end Behavioral;
-- End ALU
